package javaConcepts;

public class If_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 400;
// checking the number is even or odd number based on the Reminder
		if (num % 2 == 0) {
          // If the number is divisible by 2 and reminder is zero
			System.out.println("The given number is Even");

		} else {
   //      if the number is not divisible by 2 and reminder is not zero
			System.out.println("The Given number is Odd");
		}

	}

}
